mapper.select:
select a.* from work_order a


        and a.order_no like concat('%',#{order.orderNo},'%')


        and a.order_type = #{order.orderType}


        and a.title like concat('%',#{order.title},'%')


        and a.handle_dept_id = #{order.handleDeptId}


        and a.create_time >= #{order.createTimeStart}



        and a.create_time < #{order.createTimeEnd}


        and a.fenpai_time >= #{order.fenpaiTimeStart}



        and a.fenpai_time < #{order.fenpaiTimeEnd}

SELECT DATE(w.create_time) AS time, COUNT(w.id) AS total_orders, COALESCE(SUM(CASE WHEN w.is_overdue = 1 THEN 1 ELSE 0 END) / NULLIF(COUNT(w.id), 0), 0) AS overdue_rate FROM work_order w
and w.create_time >= #{param.startTime}
and w.create_time < #{param.endTime}
GROUP BY DATE(w.create_time) ORDER BY DATE(w.create_time);

SELECT
d.dept_name AS department_name,
COUNT(w.id) AS total_orders,
COALESCE(
SUM(CASE WHEN w.is_overdue = 1 THEN 1 ELSE 0 END) / NULLIF(COUNT(w.id), 0), 0
) AS overdue_rate
FROM
department d
LEFT JOIN work_order w ON d.dept_id = w.handle_dept_id


        and w.create_time >= #{param.startTime}



        and w.create_time < #{param.endTime}


GROUP BY
d.dept_name;

SELECT w.order_type AS order_type, COUNT(w.id) AS total_orders, COALESCE(SUM(CASE WHEN w.is_overdue = 1 THEN 1 ELSE 0 END) / NULLIF(COUNT(w.id), 0), 0) AS overdue_rate FROM work_order w
and w.create_time >= #{param.startTime}
and w.create_time < #{param.endTime}
GROUP BY w.order_type ORDER BY w.order_type;
